<template>    <div class="admin-item-edit">        <h3 class="admin-item-title" v-text="props.editItem[1].name"/>        <div class="item">            <span v-text="defaultParam" v-if="!isChange"/>            <input              v-else              class="input edit-input"              type="text"              v-model="defaultParam"            />            <v-button :text="isChange ? 'Сохранить' : 'Изменить'" @click.prevent="changeParam"/>            <v-button v-if="isChange" :text="'Отменить'" @click.prevent="cancelChange"/>        </div>    </div></template><script setup>import VButton from "~/components/ui/v-button.vue";import { useStore } from "~/store";const props = defineProps({   editItem: {      type: Object,      default: {}   }})const store = useStore()const defaultParam = ref(props.editItem[1].param)const isChange = ref(false)const cancelChange = () => {   isChange.value = false   defaultParam.value = props.editItem[1].param}const changeParam = () => {   if (isChange.value) {      const newObject = {         ...props.editItem[1],         param: defaultParam.value,      }      store.saveChange(newObject, null, 'setting', props.editItem[1].indexDB, 'site-setting')      isChange.value = false   } else {      isChange.value = true   }}</script>