<template>    <div class="admin-item-edit">        <h3 class="admin-item-title" v-text="editItem[1].name"/>        <div class="item">            <span v-text="defaultParam" v-if="!isChange"/>            <input              v-else              class="input edit-input"              type="text"              v-model="defaultParam"            />            <v-button :text="isChange ? 'Сохранить' : 'Изменить'" @click.prevent="changeParam"/>            <v-button v-if="isChange" :text="'Отменить'" @click.prevent="cancelChange"/>        </div>    </div></template><script>import VButton from "~/components/ui/v-button.vue";import { useStore } from "~/store";export default {   name: "admin-edit",   components: {VButton},   data() {      return {         defaultParam: this.editItem[1].param,         isChange: false,      }   },   props: {      editItem: {         type: Object,         default: {}      }   },   methods: {      cancelChange() {         this.isChange = false         this.defaultParam = this.editItem[1].param      },      changeParam() {         if (this.isChange) {            const newObject = {               ...this.editItem[1],               param: this.defaultParam,            }            useStore().changeSiteSetting(toRaw(newObject), this.editItem[0])            this.isChange = false         } else {            this.isChange = true         }      },   },   watch: {},}</script>